Script.script:
function sysSendAllProfiles(_, text)
  expandAlias(text)
end

ScriptGroup.script:
function initialize_spell_timer()
	local spell_timer = {}
	spell_timer.timers = {}
	return spell_timer
end

spell_timer = spell_timer or initialize_spell_timer()

spell_timer.group = "Spell Timer"

spell_timer.colour = "cyan"

