function spell_timer.get_trigger_stop_code(spell) local code = [[ local <NAME> = spell_timer.get_timer("<NAME>")
<NAME>.stop_time = time.get_time() <NAME>.last_elapsed_time = <NAME>.elapsed_time <NAME>.elapsed_time = <NAME>.stop_time - <NAME>.start_time
local time = time.convert_seconds_to_hms(<NAME>.elapsed_time) echo("\nThe spell lasted " .. time .. ".\n") ]] code = string.gsub(code, "<NAME>", spell.name) return code end

function spell_timer.truncate(exact)
	return tonumber(string.format("%.3f", exact))
end

function time.get_time()
	local current_time_as_string = getTime(true)

	return datetime:parse(current_time_as_string, "%Y.%m.%d %H:%M:%S", true)
end
